from typing import Dict, Any, Callable, get_type_hints
from inspect import signature
import logging

logger = logging.getLogger(__name__)

def generate_openapi_docs(route_func: Callable, path: str, method: str = 'GET') -> Dict[str, Any]:
    """
    Generate OpenAPI 3.0 specification for a Flask route.

    Args:
        route_func: Flask route handler function
        path: URL path (e.g., '/api/users/{id}')
        method: HTTP method

    Returns:
        OpenAPI path item dictionary

    Raises:
        ValueError: If function lacks proper type hints
    """
    try:
        hints = get_type_hints(route_func)
        sig = signature(route_func)

        params = []
        for param_name, param in sig.parameters.items():
            if param_name not in hints:
                logger.warning(f"Missing type hint for parameter: {param_name}")
                continue

            param_schema = {
                'name': param_name,
                'in': 'path' if f'{{{param_name}}}' in path else 'query',
                'required': param.default == param.empty,
                'schema': {'type': _map_type(hints[param_name])}
            }
            params.append(param_schema)

        return_type = hints.get('return', dict)

        spec = {
            method.lower(): {
                'summary': route_func.__doc__ or 'No description',
                'parameters': params,
                'responses': {
                    '200': {
                        'description': 'Success',
                        'content': {
                            'application/json': {
                                'schema': {'type': _map_type(return_type)}
                            }
                        }
                    },
                    '400': {'description': 'Bad Request'},
                    '500': {'description': 'Internal Server Error'}
                }
            }
        }

        return spec

    except Exception as e:
        logger.error(f"Failed to generate OpenAPI docs: {e}")
        raise ValueError(f"Cannot generate docs for {route_func.__name__}: {e}")


def _map_type(python_type: type) -> str:
    """Map Python types to OpenAPI types."""
    mapping = {
        int: 'integer',
        str: 'string',
        float: 'number',
        bool: 'boolean',
        dict: 'object',
        list: 'array'
    }
    return mapping.get(python_type, 'string')
