from typing import get_type_hints, Dict, Any
from inspect import signature, Parameter

def generate_openapi(func, path: str, method: str = 'GET') -> Dict[str, Any]:
    """
    Auto-generate OpenAPI spec for a Flask route.

    :param func: Route handler function
    :param path: API path (e.g., '/users/{user_id}')
    :param method: HTTP method
    :return: OpenAPI specification dict
    :raises TypeError: If function has no type hints
    """
    if not callable(func):
        raise TypeError('First argument must be a callable')

    try:
        type_hints = get_type_hints(func)
        sig = signature(func)
    except Exception as e:
        raise TypeError(f'Cannot introspect function: {e}')

    parameters = []
    for name, param in sig.parameters.items():
        param_type = type_hints.get(name, str)

        parameters.append({
            'name': name,
            'in': 'path' if f'{{{name}}}' in path else 'query',
            'required': param.default == Parameter.empty,
            'schema': {'type': map_python_to_openapi(param_type)}
        })

    return_type = type_hints.get('return', dict)

    return {
        path: {
            method.lower(): {
                'operationId': func.__name__,
                'summary': func.__doc__ or '',
                'parameters': parameters,
                'responses': {
                    '200': {
                        'description': 'OK',
                        'content': {
                            'application/json': {
                                'schema': {'type': map_python_to_openapi(return_type)}
                            }
                        }
                    }
                }
            }
        }
    }

def map_python_to_openapi(py_type) -> str:
    """Map Python types to OpenAPI types."""
    mapping = {
        int: 'integer',
        str: 'string',
        float: 'number',
        bool: 'boolean',
        list: 'array',
        dict: 'object'
    }
    return mapping.get(py_type, 'string')
