from typing import Dict, Any, Optional, get_type_hints
from inspect import signature, getdoc
import json

def generate_openapi_for_route(
    route_function,
    path: str,
    method: str = "GET",
    description: Optional[str] = None
) -> Dict[str, Any]:
    """
    Generate OpenAPI 3.0 documentation for a Flask route function.

    Args:
        route_function: The Flask route handler function
        path: The API path (e.g., '/users/{user_id}')
        method: HTTP method (default: 'GET')
        description: Optional description (uses docstring if not provided)

    Returns:
        Dictionary containing OpenAPI specification for this route

    Raises:
        ValueError: If route function is invalid or missing required information
        TypeError: If type hints are malformed

    Example:
        >>> @app.route('/users/<int:user_id>', methods=['GET'])
        >>> def get_user(user_id: int) -> dict:
        ...     '''Retrieve a user by ID'''
        ...     return {'id': user_id, 'name': 'John'}
        >>>
        >>> spec = generate_openapi_for_route(get_user, '/users/{user_id}', 'GET')
    """

    if not callable(route_function):
        raise ValueError("route_function must be a callable function")

    try:
        # Extract function metadata
        sig = signature(route_function)
        type_hints = get_type_hints(route_function)
        docstring = getdoc(route_function) or description or "No description provided"

        # Initialize OpenAPI path item
        path_item: Dict[str, Any] = {
            method.lower(): {
                "summary": docstring.split('\n')[0],  # First line as summary
                "description": docstring,
                "operationId": route_function.__name__,
                "parameters": [],
                "responses": {}
            }
        }

        operation = path_item[method.lower()]

        # Extract parameters from function signature
        for param_name, param in sig.parameters.items():
            if param_name in type_hints:
                param_type = type_hints[param_name]

                # Map Python types to OpenAPI types
                openapi_type = _python_type_to_openapi(param_type)

                # Determine parameter location
                if f"{{{param_name}}}" in path:
                    param_location = "path"
                    required = True
                else:
                    param_location = "query"
                    required = param.default == param.empty

                param_spec = {
                    "name": param_name,
                    "in": param_location,
                    "required": required,
                    "schema": {
                        "type": openapi_type
                    }
                }

                # Add default value if exists
                if param.default != param.empty:
                    param_spec["schema"]["default"] = param.default

                operation["parameters"].append(param_spec)

        # Extract return type for response schema
        if "return" in type_hints:
            return_type = type_hints["return"]
            response_schema = _generate_response_schema(return_type)

            operation["responses"]["200"] = {
                "description": "Successful response",
                "content": {
                    "application/json": {
                        "schema": response_schema
                    }
                }
            }
        else:
            operation["responses"]["200"] = {
                "description": "Successful response"
            }

        # Add common error responses
        operation["responses"]["400"] = {
            "description": "Bad request"
        }
        operation["responses"]["404"] = {
            "description": "Not found"
        }
        operation["responses"]["500"] = {
            "description": "Internal server error"
        }

        return path_item

    except Exception as e:
        raise TypeError(f"Error generating OpenAPI spec: {str(e)}") from e


def _python_type_to_openapi(python_type: type) -> str:
    """
    Convert Python type hints to OpenAPI type strings.

    Args:
        python_type: Python type object

    Returns:
        OpenAPI type string
    """
    type_mapping = {
        int: "integer",
        float: "number",
        str: "string",
        bool: "boolean",
        list: "array",
        dict: "object"
    }

    # Handle basic types
    if python_type in type_mapping:
        return type_mapping[python_type]

    # Handle typing module types
    type_str = str(python_type)
    if "List" in type_str or "list" in type_str:
        return "array"
    elif "Dict" in type_str or "dict" in type_str:
        return "object"

    return "string"  # Default fallback


def _generate_response_schema(return_type: type) -> Dict[str, Any]:
    """
    Generate OpenAPI schema for response type.

    Args:
        return_type: Return type hint

    Returns:
        OpenAPI schema dictionary
    """
    if return_type == dict:
        return {"type": "object"}
    elif return_type == list:
        return {
            "type": "array",
            "items": {"type": "object"}
        }
    else:
        return {"type": _python_type_to_openapi(return_type)}


# Example usage
if __name__ == "__main__":
    # Example Flask route function
    def get_user(user_id: int, include_posts: bool = False) -> dict:
        """
        Retrieve a user by ID.

        Optionally include their posts.
        """
        return {"id": user_id, "name": "John Doe"}

    # Generate OpenAPI documentation
    try:
        openapi_spec = generate_openapi_for_route(
            get_user,
            "/users/{user_id}",
            "GET"
        )

        print(json.dumps(openapi_spec, indent=2))
    except (ValueError, TypeError) as e:
        print(f"Error: {e}")
```

**Key Features:**

1. **Type Hints**: Uses `typing.get_type_hints()` to extract parameter and return types
2. **Error Handling**: Raises `ValueError` for invalid functions, `TypeError` for type issues
3. **Automatic Parameter Detection**: Distinguishes path vs query parameters
4. **Response Schema Generation**: Creates schemas based on return type hints
5. **Docstring Integration**: Uses function docstrings for descriptions
6. **Default Values**: Captures default parameter values
7. **Standard Error Responses**: Includes common HTTP error codes

